<script>
export default {
  name: "ProductList",
  data() {
    return {
      products: [],
      cartItems: [],
      showCart: false,
      categories: [
        "Immobilier",
        "Véhicules",
        "Locations de vacances",
        "Jeux",
        "Mode",
        "Maison & Jardin",
        "Famille",
        "Électronique",
        "Loisirs",
        "Autres"
      ]
    };
  },
  computed: {
    cartItemCount() {
      return this.cartItems.reduce((total, item) => total + item.quantity, 0);
    },
    totalprix() {
      return this.cartItems.reduce((total, item) => total + item.prix * item.quantity, 0).toFixed(2);
    }
  },
  created() {
    this.fetchProducts();
  },
  mounted() {
    // Ajouter un écouteur d'événements pour le bouton "Immobilier"
    const immobilierButton = document.getElementById('immobilier-button');
    if (immobilierButton) {
      immobilierButton.addEventListener('click', () => {
        alert('test');
      });
    }
  },
  methods: {
    async fetchProducts() {
      try {
        const response = await fetch('http://localhost:3000/product', {
          headers: {
            role: 'client'
          }
        });

        if (!response.ok) {
          throw new Error('Erreur de récupération des produits');
        }

        this.products = await response.json();
      } catch (error) {
        console.error('Erreur lors de la récupération des produits:', error);
      }
    },
    addToCart(product) {
      const existingItem = this.cartItems.find(item => item.id === product.id);
      
      if (existingItem) {
        if (existingItem.quantity < 5) {
          existingItem.quantity += 1;
        } else {
          alert('La quantité maximale pour cet article est de 5');
        }
      } else {
        this.cartItems.push({ ...product, quantity: 1 });
      }
    },
    removeFromCart(productId) {
      this.cartItems = this.cartItems.filter(item => item.id !== productId);
    },
    goToCart() {
      this.showCart = true;
    },
    closeCart() {
      this.showCart = false;
    },
    filteredProducts(categoryId) {
      // Utilise categoryId pour filtrer les produits par leur ID de catégorie
      return this.products.filter(product => product.category_id === categoryId);
    }
  }
};
</script>

